"use strict";

var mongoose = require("mongoose");
var formSchema = new mongoose.Schema({
  dateOfAdmission: {
    type: String
  },
  admissionFor: {
    type: String
  },
  particularsOfChild: {
    fullName: {
      type: String
    },
    surname: {
      type: String
    },
    nameUsedAtHome: {
      type: String
    },
    nationality: {
      type: String
    },
    dob: {
      type: String
    },
    gender: {
      type: String
    },
    language: {
      type: String
    },
    otherLanguage: {
      type: String,
      "default": ""
    },
    age: {
      type: String
    },
    houseNo: {
      type: String
    },
    buildingName: {
      type: String,
      "default": ""
    },
    streetName: {
      type: String
    },
    city: {
      type: String
    },
    pincode: {
      type: String
    }
  },
  particularsOfParents: {
    FatherName: {
      type: String
    },
    FatherQualification: {
      type: String
    },
    FatherOccupation: {
      type: String
    },
    FatherOrganisationName: {
      type: String
    },
    FatherSTDCodeRes: {
      type: String,
      "default": ""
    },
    FatherTelRes: {
      type: String,
      "default": ""
    },
    FatherSTDCodeOff: {
      type: String,
      "default": ""
    },
    FatherTelOff: {
      type: String,
      "default": ""
    },
    FatherMobile: {
      type: String
    },
    FatherEmail: {
