"use strict";

var mongoose = require("mongoose");
var formSchema = new mongoose.Schema({
  dateOfAdmission: {
    type: String
  },
  admissionFor: {
    type: String
  },
  particularsOfChild: {
    fullName: {
      type: String
    },
    surname: {
      type: String
    },
    nameUsedAtHome: {
