const BillSchema = new mongoose.Schema({
  invoiceNo: { type: String,  },
  fullName: String,
  admissionFor: String,
  feeAmount: Number,
  amountReceived: Number,
  cashReceivedFrom: String,
  relationship: String,
  date: { type: Date, default: Date.now },
});

module.exports = mongoose.model('Bill', BillSchema);

