sourcesContent:
// controllers/billController.js
const Bill = require('../module/bill');

exports.createBill = async (req, res) => {
  console.log("Received bill data:", req.body); // Log the incoming data
  try {
    const { invoiceNo, studentId, amountPaid, remainingAmount } = req.body;

    // Validate required fields
    if (!invoiceNo || !studentId || amountPaid === undefined || remainingAmount === undefined) {
      return res.status(400).json({ success: false, message: "All fields are required" });
    }

    // Create a new bill instance
    const newBill = new Bill({
      invoiceNo,
      studentId,
      amountPaid,
      remainingAmount
    });

    await newBill.save(); // Save to the database

    res.status(201).json({ success: true, message: "Bill submitted successfully", data: newBill });
  } catch (error) {
    console.error("Error submitting bill:", error); // Log the error
    res.status(500).json({ success: false, message: "Failed to submit bill", error: error.message });
  }
};

